import React, { useState } from 'react'
import { Form } from 'react-bootstrap'
import Button from 'react-bootstrap/Button'


/* 1st form
const Add = ({onSubmit, inputValue, onChange}) => {
    return(
        <div className="container">
           <div>
               <form onSubmit={onSubmit}>
                   <input value={inputValue} onChange={onChange} />
                   <button type="submit">Save Node</button>
               </form>
           </div>
        </div>
    )
}
*/

const Add = ({onSubmit, inputValue, onChange}) => {
    /*
    const [nodes, setNodes] = useState([])
    const [newNode, setNewNode] = useState('Give node a name...')

    const addNode = (event) => {
        event.preventDefault()
        const nodeObject = {
            content: newNode
        }
        axios.post('http://localhost:3001/nodes', nodeObject)
        .then(response => {
            setNodes(nodes.concat(response.data))
            setNewNode('')
        })
    }

    const handleNoteChange = (event) => {
        console.log(event.target.value)
        setNewNode(event.target.value)
    }

    useEffect(() => {
        axios.get('http://localhost:3001/nodes')
        .then(response => {
            setNodes(response.data)
        })
    }, [])
*/
    const [validated, setValidated] = useState(false)

    const handleSubmitValidation = (event) => {
        const form = event.currentTarget
        console.log("Submit 1: ", form.checkValidity() + ", " + validated)
        if(form.checkValidity() === false) {
            event.preventDefault()
            event.stopPropagation()
            console.log("Validity false")
        }

        setValidated(true)
        console.log("Submit 2: ", form.checkValidity() + ", " + validated)
    }

    return(
        <div className='container'>
            <Form noValidate validated={validated} onSubmit={handleSubmitValidation}>
                <Form.Group className='mb-3' controlId='formContent'>
                    <Form.Label>Content</Form.Label>
                    <Form.Control hasValidation type='text' placeholder='Enter content' required/>
                    <Form.Control.Feedback type='invalid'>Please insert content.</Form.Control.Feedback>
                </Form.Group>
                <Button variant='primary' type='submit'>Add</Button>
            </Form>
        </div>
    )
}

export default Add
