import axios from 'axios'
import React, {useState} from 'react'


const Add = () => {
    const {newNode, setNewNode} = useState('Give node a name...')
    const {nodes, setNodes} = useState([])
    const addNode = (event) => {
        event.preventDefault()
        const nodeObject = {
            content: newNode
        }
        axios.post('http://localhost:3001/nodes', nodeObject)
        .then(response => {
            setNodes(nodes.concat(response.data))
            setNewNode('Give node a name...')
        })
    }
    return(
        <div className="container">
           <div>
               <form onSubmit={addNode}>
                   <input value={newNode} />
                   <button type="submit">Save Node</button>
               </form>
           </div>
        </div>
    )
}

export default Add
