/*const Home = () => {
    return(
        <div>
            <p>Koti</p>
        </div>
    )
}*/

const App = () => {
    const padding = {
        padding: 5
    }

    const [nodes, setNodes] = useState([])
    const [newNode, setNewNode] = useState('Give node a name...')

    const addNode = (event, validation) => {
        event.preventDefault()
        if(validation === false) {
            return
        }
        const nodeObject = {
            content: newNode
        }
        axios.post('http://localhost:3001/nodes', nodeObject)
        .then(response => {
            setNodes(nodes.concat(response.data))
            setNewNode('')
        })
    }

    const handleNoteChange = (event) => {
        console.log(event.target.value)
        setNewNode(event.target.value)
    }

    useEffect(() => {
        axios.get('http://localhost:3001/nodes')
        .then(response => {
            setNodes(response.data)
        })
    }, [])

    return (
        <div className="container">
            <Router>
                <div>
                    <Link style={padding} to="/">home</Link>
                    <Link style={padding} to="/add">add</Link>
                    <Link style={padding} to="/list">list</Link>
                </div>

                <Switch>
                    <Route path="/add">
                        <Add onSubmit={addNode} inputValue={newNode} onChange={handleNoteChange}/>
                    </Route>
                    <Route path="/list">
                        <List nodes={nodes}/>
                    </Route>
                    <Route path="/">
                        <Home />
                    </Route>
                </Switch>

                <div>
                    <i>Esimerkkivalikko </i>
                    <i>perustuu HY:n fullstackopen-kurssimateriaaliin</i>
                </div>
            </Router>
        </div>
    )
}

export default App
